#include <iostream>
#include <sstream>

using namespace std;

bool has_repeated_pair(string pair, string s, uint64_t from, uint64_t to) {
    for(uint64_t i = from; i < to - 1; i++) {
        if(s[i] == pair[0] && s[i + 1] == pair[1]) {
            return true;
        }
    }
    return false;
}

bool is_nice(string s) {
    int pair_c = 0;
    int repeated_c = 0;

    for(uint64_t i = 0; i < s.size() - 1; i++) {
        if(i-0 >= 2)
            pair_c += has_repeated_pair(&s[i], s, 0, i);
        if(s.size() - i >= 4)
            pair_c += has_repeated_pair(&s[i], s, i + 2, s.size());
        if(s[i + 2] == s[i])
            repeated_c++;
    }

    return pair_c > 0 && repeated_c > 0;
}

